CREATE TABLE single_storages (DEVICE_ID INTEGER AUTO_INCREMENT NOT NULL, storage_type VARCHAR(31), MODEL_NO VARCHAR(255) NOT NULL, SERIAL_NO VARCHAR(255) NOT NULL UNIQUE, CAPACITY INTEGER NOT NULL, WARRANTY INTEGER NOT NULL, CINTERFACE INTEGER, SPEED_CLASS INTEGER, WEIGHT FLOAT, COLOR INTEGER, DESCRIPTION VARCHAR(255), FORM_FACTOR FLOAT, RPMS INTEGER, PRIMARY KEY (DEVICE_ID))
CREATE TABLE joined_storages (DEVICE_ID INTEGER AUTO_INCREMENT NOT NULL, DTYPE VARCHAR(31), MODEL_NO VARCHAR(255) NOT NULL, SERIAL_NO VARCHAR(255) NOT NULL UNIQUE, CAPACITY INTEGER NOT NULL, WARRANTY INTEGER NOT NULL, PRIMARY KEY (DEVICE_ID))
CREATE TABLE joined_sdcards (DEVICE_ID INTEGER NOT NULL, CINTERFACE INTEGER, SPEED_CLASS INTEGER, WEIGHT FLOAT, PRIMARY KEY (DEVICE_ID))
CREATE TABLE joined_internal_hdds (DEVICE_ID INTEGER NOT NULL, CINTERFACE INTEGER, COLOR INTEGER, DESCRIPTION VARCHAR(255), FORM_FACTOR FLOAT, RPMS INTEGER, PRIMARY KEY (DEVICE_ID))
CREATE TABLE concrete_storages (DEVICE_ID INTEGER AUTO_INCREMENT NOT NULL, MODEL_NO VARCHAR(255) NOT NULL, SERIAL_NO VARCHAR(255) NOT NULL UNIQUE, CAPACITY INTEGER NOT NULL, WARRANTY INTEGER NOT NULL, PRIMARY KEY (DEVICE_ID))
CREATE TABLE concrete_sdcards (DEVICE_ID INTEGER AUTO_INCREMENT NOT NULL, MODEL_NO VARCHAR(255) NOT NULL, SERIAL_NO VARCHAR(255) NOT NULL UNIQUE, CAPACITY INTEGER NOT NULL, CINTERFACE INTEGER, SPEED_CLASS INTEGER, WARRANTY INTEGER NOT NULL, WEIGHT FLOAT, PRIMARY KEY (DEVICE_ID))
CREATE TABLE concrete_internal_hdds (DEVICE_ID INTEGER AUTO_INCREMENT NOT NULL, MODEL_NO VARCHAR(255) NOT NULL, SERIAL_NO VARCHAR(255) NOT NULL UNIQUE, CAPACITY INTEGER NOT NULL, CINTERFACE INTEGER, COLOR INTEGER, DESCRIPTION VARCHAR(255), FORM_FACTOR FLOAT, RPMS INTEGER, WARRANTY INTEGER NOT NULL, PRIMARY KEY (DEVICE_ID))
ALTER TABLE joined_sdcards ADD CONSTRAINT FK_joined_sdcards_DEVICE_ID FOREIGN KEY (DEVICE_ID) REFERENCES joined_storages (DEVICE_ID)
ALTER TABLE joined_internal_hdds ADD CONSTRAINT FK_joined_internal_hdds_DEVICE_ID FOREIGN KEY (DEVICE_ID) REFERENCES joined_storages (DEVICE_ID)
